mapper.update:
update tb_user_message


        name=#{name},


        sex=#{sex},


        birth=#{birth},


        phone=#{phone}


where id=#{id}

update tb_user_message
set avatar=#{avatar}
where id = #{id}

mapper.select:
select salt
from tb_login_user
where id = #{id}

select password
from tb_login_user
where id = #{id}

mapper.update:
update tb_login_user
set salt=#{newSalt},
    password=#{newPassword}
where id = #{id}
